	//obtiene los datos del correo emisor del mensaje de confirmacion y la contraseña
	var consultarCorreoEmisor = function(){
		var correo;
		var contraseña;
		$.ajax({
			type:"GET",
			url: urlMiPagina+"/administrador/login",
			dataType:"json",
	    	contentType:"text/plain"
		}).done(function(msg){
			for( var dato in msg){
				correo = msg[dato].correo;
				contraseña = msg[dato].contraseñaCorreo;	
			}
			enviarCorreoConfirmacion(correo,contraseña)					
		});
		
	};
	// envia el correo de confirmación al postulante
	var enviarCorreoConfirmacion = function(correoEmisor,contraseñaCorreo){
		var nombre = $("#txtNombrePrincipal"), 
			apellido = $("#txtApellidoPrincipal"),
			correo = $("#txtCorreoPrincipal"),
			contenedorError = $("#errorPrincipal");

		var datosValidar = [nombre, apellido, correo];
		
		var validacion1 = validarCamposVacios(datosValidar);
		var validacion2 = validarCampoCorreo(correo);	

		
		if (!validacion1 && !validacion2) {
			datos = { nombre:nombre.val(), apellido:apellido.val(), correo:correo.val() , correoEmisor:correoEmisor, contraseñaCorreo:contraseñaCorreo }			
			$.ajax({
				type:"POST",
				url: urlMiPagina+"/correo",
				dataType:"text",
				contentType:"application/json",
				data: JSON.stringify(datos)
			}).done(function(msg){				
			});		
			$("#idcorreo").html(correo.val());
			$('#cuadroConfirmacion').modal('show');
			localStorage.setItem("correo", correo.val());
			
			nombre.val("")
			apellido.val("")
			correo.val("")			
		}		
		else{
			if (validacion1 && validacion2) { 
				mostrarMensajeError(contenedorError,"Por favor complete los campos vacíos e ingrese una dirección de correo válida.")
			}
			else{
				if (validacion1) { mostrarMensajeError(contenedorError,"Por favor complete los campos vacíos.") }
				if (validacion2) { mostrarMensajeError(contenedorError,"Ingrese una dirección de correo válida.") }
			}
		}
	};

	// llamada a la funcion enviar correo de confirmacion 
	$("#btnValidarPrincipal").click(function(){			
		consultarCorreoEmisor();
	});
