	/*--Llenar select: fecha de nacimiento*/
	var llenarfechaNacimiento = function (curso) {
		// declaramos las variables
		var edadMin, edadMax, añoMin, añoMax;
		var fecha = new Date();
		var añoActual = fecha.getFullYear();
		// consultamos los cursos 
		$.ajax({
	    	type:"GET",
	    	url: urlMiPagina+"/curso",
	    	dataType:"json",
	    	contentType:"text/plain"
    	}).done(function(msg){
	   		for(var dato in msg){
	   			// traemos la edad minima y maxima del curso elegido
	   			if (curso == msg[dato].id) {
		   			edadMin = msg[dato].edad_Min;
		   			edadMax = msg[dato].edad_Max;	
	   			}
			}
			// obtenemos el rango de año disponibles para el curso
	    	añoMin = añoActual - edadMin;
	    	añoMax = añoActual - edadMax;

			for (var dia = 1; dia < 32; dia++) {
				$("#selectDia").append(new Option(dia, dia))
			}

			for (var mes = 1; mes < 13; mes++) {
				$("#selectMes").append(new Option(mes, mes))
			}	

			for ( añoMin ; añoMin > añoMax; añoMin--) {
				$("#selectAño").append(new Option(añoMin, añoMin))
			}
			$("#rangoEdad").html("debe tener entre "+edadMin+" a "+edadMax+" años para acceder a este curso")			 
		});	
	}
	//obtener edad mediante la fecha de nacimiento
	var obtenerEdad = function(){
		var fecha = new Date();
		var añoActual = fecha.getFullYear(), diaActual = fecha.getDate(), mesActual = (fecha.getMonth()+1);
		var dia = $("#selectDia").val(), mes = $("#selectMes").val(), año = $("#selectAño").val();	

		var edad = añoActual - año;
		if(mesActual < mes ){
			edad = edad - 1
		}
		else{
			if(diaActual < dia){
				edad = edad - 1
			}
		}
		$("#txtEdad").val(edad)
	}
	

