	//obtiene los datos del correo emisor del mensaje de confirmacion y la contrasea
	var consultarCorreoEmisor = function(){
		var correo;
		var contrasea;
		$.ajax({
			type:"GET",
			url: urlMiPagina+"/administrador/login",
			dataType:"json",
	    	contentType:"text/plain"
		}).done(function(msg){
			for( var dato in msg){
				correo = msg[dato].correo;
				contrasea = msg[dato].contraseaCorreo;	
			}
			enviarCorreoConfirmacion(correo,contrasea)					
		});
		
	};
	// envia el correo de confirmacin al postulante
	var enviarCorreoConfirmacion = function(correoEmisor,contraseaCorreo){
		var nombre = $("#txtNombrePrincipal"), 
			apellido = $("#txtApellidoPrincipal"),
			correo = $("#txtCorreoPrincipal"),
			contenedorError = $("#errorPrincipal");

		var datosValidar = [nombre, apellido, correo];
		
		var validacion1 = validarCamposVacios(datosValidar);
		var validacion2 = validarCampoCorreo(correo);	

		
		if (!validacion1 && !validacion2) {
			datos = { nombre:nombre.val(), apellido:apellido.val(), correo:correo.val() , correoEmisor:correoEmisor, contraseaCorreo:contraseaCorreo }			
			$.ajax({
				type:"POST",
				url: urlMiPagina+"/correo",
				dataType:"text",
				contentType:"application/json",
				data: JSON.stringify(datos)
			}).done(function(msg){				
			});		
			$("#idcorreo").html(correo.val());
			$('#cuadroConfirmacion').modal('show');
			localStorage.setItem("correo", correo.val());
			
			nombre.val("")
			apellido.val("")
			correo.val("")			
		}		
		else{
			if (validacion1 && validacion2) { 
				mostrarMensajeError(contenedorError,"Por favor complete los campos vacos e ingrese una direccin de correo vlida.")
			}
			else{
				if (validacion1) { mostrarMensajeError(contenedorError,"Por favor complete los campos vacos.") }
				if (validacion2) { mostrarMensajeError(contenedorError,"Ingrese una direccin de correo vlida.") }
			}
		}
	};

	// llamada a la funcion enviar correo de confirmacion 
	$("#btnValidarPrincipal").click(function(){			
		consultarCorreoEmisor();
	});
