	//Prepara los campos del formulario para mostrar los datos necesarios
	//Obtener codigo de solicitud/Postulante
	var obtenerCodigoPostulante = function(){
		$.ajax({
	    	type:"GET",
	    	url: urlMiPagina+"/estudiante",
	    	dataType:"json",
	    	contentType:"text/plain"
    	}).done(function(msg){	
	   		if (msg.length != 0) {
	   			var fecha = new Date();
				var ao, mes, semestre, id;
				var dato = msg.length - 1;
				var codigo = msg[dato].codigo;
				
				id = codigo.substring(4);
				id = Number(id) + 1;
				if(id < 10) { id = '00'+id}
				else{
					if(id < 100) { id = '0'+id}
				}
		
				//obtenemos el ao 
				ao = fecha.getFullYear();
				ao = ao.toString().substring(2);
				//obtnemos el mes y sacamos el semestre actual
				mes = fecha.getMonth()
				if(mes <= 5){ semestre = '01' }
				else{ semestre = '02' }
				// formamos nuestra formato de codigo
				nuevoCodigo = ao+semestre+'-'+id
				$("#txtIdFormulario").html(nuevoCodigo)	
	   		}
	   		else{
				var fecha = new Date();
				var ao, mes, semestre, id;
					
				id = "001"	
				//obtenemos el ao 
				ao = fecha.getFullYear();
				ao = ao.toString().substring(2);
				//obtnemos el mes y sacamos el semestre actual
				mes = fecha.getMonth()
				if(mes <= 5){ semestre = '01' }
				else{ semestre = '02' }
				// formamos nuestra formato de codigo
				nuevoCodigo = ao+semestre+'-'+id
				$("#txtIdFormulario").html(nuevoCodigo)
			}			   		
		});		
	};
	//Obtener fecha actual
	var obtenerFechaActual = function() {
		var fecha = new Date();
		var Ao = fecha.getFullYear()
		if (fecha.getDate() < 10) { var Dia = "0"+fecha.getDate() } else{ var Dia = fecha.getDate()};
		if ((fecha.getMonth()+1) < 10) { var Mes = "0"+(fecha.getMonth()+1) } else{ var Mes = (fecha.getMonth()+1)}
		var fechaActual = Dia+"/"+Mes+"/"+Ao
		
		$("#txtFechaFormulario").html(fechaActual)
	};

