	/*--Llenar select: fecha de nacimiento*/
	var llenarfechaNacimiento = function (curso) {
		// declaramos las variables
		var edadMin, edadMax, aoMin, aoMax;
		var fecha = new Date();
		var aoActual = fecha.getFullYear();
		// consultamos los cursos 
		$.ajax({
	    	type:"GET",
	    	url: urlMiPagina+"/curso",
	    	dataType:"json",
	    	contentType:"text/plain"
    	}).done(function(msg){
	   		for(var dato in msg){
	   			// traemos la edad minima y maxima del curso elegido
	   			if (curso == msg[dato].id) {
		   			edadMin = msg[dato].edad_Min;
		   			edadMax = msg[dato].edad_Max;	
	   			}
			}
			// obtenemos el rango de ao disponibles para el curso
	    	aoMin = aoActual - edadMin;
	    	aoMax = aoActual - edadMax;

			for (var dia = 1; dia < 32; dia++) {
				$("#selectDia").append(new Option(dia, dia))
			}

			for (var mes = 1; mes < 13; mes++) {
				$("#selectMes").append(new Option(mes, mes))
			}	

			for ( aoMin ; aoMin > aoMax; aoMin--) {
				$("#selectAo").append(new Option(aoMin, aoMin))
			}
			$("#rangoEdad").html("debe tener entre "+edadMin+" a "+edadMax+" aos para acceder a este curso")			 
		});	
	}
	//obtener edad mediante la fecha de nacimiento
	var obtenerEdad = function(){
		var fecha = new Date();
		var aoActual = fecha.getFullYear(), diaActual = fecha.getDate(), mesActual = (fecha.getMonth()+1);
		var dia = $("#selectDia").val(), mes = $("#selectMes").val(), ao = $("#selectAo").val();	

		var edad = aoActual - ao;
		if(mesActual < mes ){
			edad = edad - 1
		}
		else{
			if(diaActual < dia){
				edad = edad - 1
			}
		}
		$("#txtEdad").val(edad)
	}
	

