	//validacin de formulario de confirmacin de correo (pgina principal)
	var validarCamposVacios = function(datos){
		var error; 
		for(var i in datos) {
			if(!datos[i].attr("disabled")){
				if (datos[i].val() == '' || datos[i].val() == null) {
							datos[i].addClass('input-error');
							error = true;
				}
			}
		}
		return error;
	};
	//valida que el campo correo tenga un formato correcto
	var validarCampoCorreo = function(correo){
		var regex = /[\w-\.]{2,}@([\w-]{2,}\.)*([\w-]{2,}\.)[\w-]{2,4}/;
		var error;

		if (regex.test(correo.val().trim())) { error = false } 
        else { error = true }   

        return error
	};
	//validar solo 10 numeros
	var validarCamposNumericos = function(datos){
		var error
		for(var i in dato) {	
			if (dato[i].val().length != 10) {
						dato[i].addClass('input-error');
						error = true
			}
		}
	}

	//validar solo numero en los campos
	$(".solonumero").keypress(function(e){
		key = e.keyCode || e.which;
		tecla = String.fromCharCode(key).toLowerCase();
		numeros = " 1234567890";
		especiales = "8-37-39-46";
		tecla_especial = false;
		for(var i in especiales){
			if(key == especiales[i]){
				tecla_especial = true;
				break;
            }
        }
        if(numeros.indexOf(tecla)==-1 && !tecla_especial){return false}
	})

	var validarCampoEnterosPositivos = function(dato){
		var error
		for(var i in dato) {
			if (dato[i].attr("type") == "number") {
				if (dato[i].val() < 0 ) {
						dato[i].addClass('input-error');
						error = true;
				}
			}
		}
		return error;
	}

