	/*Mostrar o Ocultar acceso a administrador*/
	var menustate = 0; 
    $("#linkdesp").click(function(){
        if (menustate === 0){
           $("#cuadroOculto").slideToggle("slow"); 
           $("#iconOculto").addClass("glyphicon-minus"); 
           $("#iconOculto").removeClass("glyphicon-plus");
           $("#iconOculto").addClass("icon-visible"); 
           $("#iconOculto").removeClass("icon-oculto");
           $("#linkdesp").addClass("border-visible");
           $("#linkdesp").removeClass("border-oculto");  
           menustate=1;
        }
        else if (menustate === 1){
           
            $("#cuadroOculto").slideToggle("slow");
            $("#iconOculto").addClass("icon-oculto"); 
            $("#iconOculto").removeClass("icon-visible");
            $("#iconOculto").addClass("glyphicon-plus"); 
            $("#iconOculto").removeClass("glyphicon-minus");
            $("#linkdesp").addClass("border-oculto");
            $("#linkdesp").removeClass("border-visible");        
            menustate=0;
        }
    });

	
	// efecto padallax e invisibilidad de texto
	$(window).scroll(function(){
		var barra = $(window).scrollTop();
		var posicion =  (barra * 0.20);
		var transparencia =  1-(barra * 0.0023);
		var posicionHeader = (parseInt($("header").css("height")))-58
		
		//agrega el efecto padallax
		$('#body').css({
			'background-position': '0 -'+ posicion +'px'
		});

		//agrega el efecto de invisibilad de texto
		$('.div-invisible').css({
			'opacity':''+transparencia+''
		})
	});


